const { createSlice } = require("@reduxjs/toolkit");

const initialState = {
    showCart: true
}

const cartSlice = createSlice({
    name: 'cart',
    initialState,
    reducers: {
        toggle: (state,action) => {
            state.showCart = !state.showCart;
        },
    }
});

export const actions = cartSlice.actions;
export default cartSlice.reducer;
